from bs4 import BeautifulSoup

def export_results_to_txt(results_list, filename="scraped_tabs.txt"):
    """
    Exports a list of dictionaries (containing 'url' and 'name') to a plain text file.

    Args:
        results_list (list): A list of dictionaries, where each dictionary
                             represents a scraped tab (e.g., [{'url': '...', 'name': '...'}]).
        filename (str): The name of the file to save the results to.
    """
    try:
        # 'w' mode means write (will overwrite the file if it exists)
        with open(filename, 'w', encoding='utf-8') as f:
            for result in results_list:
                # Iterate through the (key, value) pairs in each dictionary
                for key, value in result.items():
                    # Write the formatted key-value pair, followed by a newline
                    f.write(f"{key}: {value}\n")

                # Add a separator line for readability between different entries
                f.write("\n" + "="*40 + "\n\n")

        print(f"✅ Successfully exported {len(results_list)} entries to {filename}")

    except IOError as e:
        print(f"❌ An error occurred while writing to the file: {e}")

def extract_tab_info(html_content):
    """
    Parses the HTML from chrome://inspect/#pages to extract the URL and Name
    for each inspectable page.

    Args:
        html_content (str): The raw HTML content of the chrome://inspect/#pages.

    Returns:
        list: A list of dictionaries, each containing 'url' and 'name'.
    """
    soup = BeautifulSoup(html_content, 'html.parser')
    tab_data = []

    # Define the prefixes for Chrome-related links to exclude
    EXCLUSIONS = (
        'chrome://',
        'chrome-extension://',
        'devtools://',
        'about:',
    )

    # Find all divs with class 'url'
    url_divs = soup.find_all('div', class_='url')

    for url_div in url_divs:
        parent = url_div.parent

        url = url_div.text.strip()

        name_div = parent.find('div', class_='name')
        name = name_div.text.strip() if name_div else "No Name Found"

        # Apply the filtering logic: Check if the URL starts with any of the exclusion prefixes
        is_internal = any(url.startswith(prefix) for prefix in EXCLUSIONS)

        if not is_internal:         
            tab_data.append({
                'name': name,
                'url': url
            })

    return tab_data

def main():
    results = extract_tab_info(html)

    for result in results:
        for key, value in result.items():
            print(f"{key} - {value}")

    export_results_to_txt(results)

if __name__ == "__main__":
    main()